
public class Whiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//While Loop
		
		/*
		 * int i = 10;
		 * 
		 * while(i>=1) {
		 * 
		 * System.out.println(i); i--;
		 * 
		 * }
		 */
		
		//do while loop
		
		int i =10;
		
		do {
			
			System.out.println(i);
			i--;
			
		}while(i>=1);
		}

	}


